server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertConversationSchema,
  insertMessageSchema,
  insertTokenUsageSchema
} from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Get current user (for demo purposes, we'll use user ID 1)
  app.get("/api/user", async (req, res) => {
    try {
      const user = await storage.getUser(1);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      res.status(500).json({ message: "Failed to get user" });
    }
  });

  // Update user
  app.patch("/api/user", async (req, res) => {
    try {
      const updates = req.body;
      const user = await storage.updateUser(1, updates);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      res.status(500).json({ message: "Failed to update user" });
    }
  });

  // Get conversations
  app.get("/api/conversations", async (req, res) => {
    try {
      const conversations = await storage.getConversations(1);
      res.json(conversations);
    } catch (error) {
      res.status(500).json({ message: "Failed to get conversations" });
    }
  });

  // Create conversation
  app.post("/api/conversations", async (req, res) => {
    try {
      const data = insertConversationSchema.parse({
        ...req.body,
        userId: 1
      });
      const conversation = await storage.createConversation(data);
      res.json(conversation);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create conversation" });
    }
  });

  // Update conversation
  app.patch("/api/conversations/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const updates = req.body;
      const conversation = await storage.updateConversation(id, updates);
      if (!conversation) {
        return res.status(404).json({ message: "Conversation not found" });
      }
      res.json(conversation);
    } catch (error) {
      res.status(500).json({ message: "Failed to update conversation" });
    }
  });

  // Delete conversation
  app.delete("/api/conversations/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const success = await storage.deleteConversation(id);
      if (!success) {
        return res.status(404).json({ message: "Conversation not found" });
      }
      res.json({ message: "Conversation deleted" });
    } catch (error) {
      res.status(500).json({ message: "Failed to delete conversation" });
    }
  });

  // Get messages for a conversation
  app.get("/api/conversations/:id/messages", async (req, res) => {
    try {
      const conversationId = parseInt(req.params.id);
      const messages = await storage.getMessages(conversationId);
      res.json(messages);
    } catch (error) {
      res.status(500).json({ message: "Failed to get messages" });
    }
  });

  // Create message
  app.post("/api/conversations/:id/messages", async (req, res) => {
    try {
      const conversationId = parseInt(req.params.id);
      const data = insertMessageSchema.parse({
        ...req.body,
        conversationId
      });
      const message = await storage.createMessage(data);

      // If this is the first user message, update conversation title
      if (data.role === "user") {
        const existingMessages = await storage.getMessages(conversationId);
        const userMessages = existingMessages.filter(msg => msg.role === "user");

        // If this is the first user message, update the conversation title
        if (userMessages.length === 1) {
          const truncatedTitle = data.content.length > 50 
            ? data.content.substring(0, 50) + "..."
            : data.content;

          await storage.updateConversation(conversationId, {
            title: truncatedTitle
          });
        }

        // Create template AI response
        setTimeout(async () => {
          await storage.createMessage({
            conversationId,
            content: `Hello, this is an AI response simulation. Are you asking about "${data.content}"?`,
            role: "assistant",
            attachments: null
          });
        }, 1000);
      }

      res.json(message);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create message" });
    }
  });

  // Get token usage
  app.get("/api/token-usage", async (req, res) => {
    try {
      const { startDate, endDate } = req.query;
      const start = startDate ? new Date(startDate as string) : undefined;
      const end = endDate ? new Date(endDate as string) : undefined;

      const usage = await storage.getTokenUsage(1, start, end);
      res.json(usage);
    } catch (error) {
      res.status(500).json({ message: "Failed to get token usage" });
    }
  });

  // Create token usage
  app.post("/api/token-usage", async (req, res) => {
    try {
      const data = insertTokenUsageSchema.parse({
        ...req.body,
        userId: 1
      });
      const usage = await storage.createTokenUsage(data);
      res.json(usage);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create token usage" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, conversations, messages, tokenUsage,
  type User, type InsertUser,
  type Conversation, type InsertConversation,
  type Message, type InsertMessage,
  type TokenUsage, type InsertTokenUsage
} from "@shared/schema";

export interface IStorage {
  // User methods
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: number, updates: Partial<User>): Promise<User | undefined>;

  // Conversation methods
  getConversations(userId: number): Promise<Conversation[]>;
  getConversation(id: number): Promise<Conversation | undefined>;
  createConversation(conversation: InsertConversation): Promise<Conversation>;
  updateConversation(id: number, updates: Partial<Conversation>): Promise<Conversation | undefined>;
  deleteConversation(id: number): Promise<boolean>;

  // Message methods
  getMessages(conversationId: number): Promise<Message[]>;
  createMessage(message: InsertMessage): Promise<Message>;

  // Token usage methods
  getTokenUsage(userId: number, startDate?: Date, endDate?: Date): Promise<TokenUsage[]>;
  createTokenUsage(usage: InsertTokenUsage): Promise<TokenUsage>;
}

export class MemStorage implements IStorage {
  private users: Map<number, User>;
  private conversations: Map<number, Conversation>;
  private messages: Map<number, Message>;
  private tokenUsage: Map<number, TokenUsage>;
  private currentUserId: number;
  private currentConversationId: number;
  private currentMessageId: number;
  private currentTokenUsageId: number;

  constructor() {
    this.users = new Map();
    this.conversations = new Map();
    this.messages = new Map();
    this.tokenUsage = new Map();
    this.currentUserId = 1;
    this.currentConversationId = 1;
    this.currentMessageId = 1;
    this.currentTokenUsageId = 1;

    // Add sample data
    this.initSampleData();
  }

  private async initSampleData() {
    // Create sample user
    const user = await this.createUser({
      username: "demo_user",
      password: "password",
      email: "demo@example.com",
      role: "Developer",
      profilePhoto: null,
    });

    // Create sample conversations
    const conv1 = await this.createConversation({
      userId: user.id,
      title: "Crude apa saja yang diolah pa...",
      isPinned: false,
    });

    // Add sample messages
    await this.createMessage({
      conversationId: conv1.id,
      content: "Crude apa saja yang diolah pada bulan Mei 2025 ?",
      role: "user",
      attachments: null,
    });

    await this.createMessage({
      conversationId: conv1.id,
      content: "Pada bulan Mei 2025, unit pengolahan di Kilang Cilacap mengolah kombinasi dari beberapa jenis minyak mentah (crude oil) untuk memenuhi spesifikasi produk dan optimasi biaya operasional.\n\nJenis Crude Oil yang Diolah:\n\nMinas Crude: Merupakan crude oil yang diproduksi secara domestik dari sumur minyak di Indonesia. Ini adalah minyak mentah yang stabil dengan kadar sulfur rendah, sering digunakan sebagai base load di kilang kami.\n\nSaudi Light Crude: Diimpor dari Arab Saudi, minyak mentah ini memiliki kandungan sulfur yang moderat dan gravitasi API yang lebih ringan, sangat ideal untuk menghasilkan bensin dan nafta berkualitas tinggi.\n\nWTI (West Texas Intermediate) Crude: Jenis minyak mentah ini berasal dari Amerika Serikat. Digunakan sebagai topping untuk meningkatkan produksi produk-produk distillate ringan seperti avtur dan kerosene.",
      role: "assistant",
      attachments: null,
    });

    // Add sample token usage
    const today = new Date();
    for (let i = 0; i < 30; i++) {
      const date = new Date(today);
      date.setDate(date.getDate() - i);
      await this.createTokenUsage({
        userId: user.id,
        tokensUsed: Math.floor(Math.random() * 5000) + 1000,
      });
    }
  }

  // User methods
  async getUser(id: number): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = this.currentUserId++;
    const user: User = { 
      ...insertUser, 
      id,
      email: insertUser.email || null,
      profilePhoto: insertUser.profilePhoto || null,
      role: insertUser.role || null,
      aiPreferences: {
        maxTokens: 4000,
        personality: 'default',
        addressStyle: 'casual'
      }
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: number, updates: Partial<User>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser = { ...user, ...updates };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  // Conversation methods
  async getConversations(userId: number): Promise<Conversation[]> {
    return Array.from(this.conversations.values())
      .filter(conv => conv.userId === userId)
      .sort((a, b) => {
        if (a.isPinned && !b.isPinned) return -1;
        if (!a.isPinned && b.isPinned) return 1;
        return new Date(b.updatedAt!).getTime() - new Date(a.updatedAt!).getTime();
      });
  }

  async getConversation(id: number): Promise<Conversation | undefined> {
    return this.conversations.get(id);
  }

  async createConversation(insertConversation: InsertConversation): Promise<Conversation> {
    const id = this.currentConversationId++;
    const now = new Date();
    const conversation: Conversation = {
      ...insertConversation,
      id,
      isPinned: insertConversation.isPinned || false,
      createdAt: now,
      updatedAt: now,
    };
    this.conversations.set(id, conversation);
    return conversation;
  }

  async updateConversation(id: number, updates: Partial<Conversation>): Promise<Conversation | undefined> {
    const conversation = this.conversations.get(id);
    if (!conversation) return undefined;

    const updatedConversation = { 
      ...conversation, 
      ...updates, 
      updatedAt: new Date() 
    };
    this.conversations.set(id, updatedConversation);
    return updatedConversation;
  }

  async deleteConversation(id: number): Promise<boolean> {
    // Delete associated messages
    Array.from(this.messages.values())
      .filter(message => message.conversationId === id)
      .forEach(message => this.messages.delete(message.id));

    return this.conversations.delete(id);
  }

  // Message methods
  async getMessages(conversationId: number): Promise<Message[]> {
    return Array.from(this.messages.values())
      .filter(message => message.conversationId === conversationId)
      .sort((a, b) => new Date(a.createdAt!).getTime() - new Date(b.createdAt!).getTime());
  }

  async createMessage(insertMessage: InsertMessage): Promise<Message> {
    const id = this.currentMessageId++;
    const message: Message = {
      ...insertMessage,
      id,
      attachments: insertMessage.attachments as string[] | null,
      createdAt: new Date(),
    };
    this.messages.set(id, message);

    // Update conversation timestamp
    await this.updateConversation(insertMessage.conversationId, { updatedAt: new Date() });

    return message;
  }

  // Token usage methods
  async getTokenUsage(userId: number, startDate?: Date, endDate?: Date): Promise<TokenUsage[]> {
    let usage = Array.from(this.tokenUsage.values())
      .filter(usage => usage.userId === userId);

    if (startDate) {
      usage = usage.filter(u => new Date(u.date!) >= startDate);
    }
    if (endDate) {
      usage = usage.filter(u => new Date(u.date!) <= endDate);
    }

    return usage.sort((a, b) => new Date(a.date!).getTime() - new Date(b.date!).getTime());
  }

  async createTokenUsage(insertUsage: InsertTokenUsage): Promise<TokenUsage> {
    const id = this.currentTokenUsageId++;
    const usage: TokenUsage = {
      ...insertUsage,
      id,
      date: new Date(),
    };
    this.tokenUsage.set(id, usage);
    return usage;
  }
}

export const storage = new MemStorage();

